import styled from "styled-components";

export const Menu = styled.div` 

    width: 200px;
    height: 200px;
    position: fixed;
    background-color: #111;
    top: 70px;
    right: 10px;

    ul li a{
        color: #fff;
        text-decoration: none;
        font-size: 20px;
    }


`
