import styled, { css } from "styled-components";


export const HighlightMoive = styled.section`

    height: 100vh;
    background-size: cover;
    background-position: center;
    ${({image})=> css`background-image: ${image};`}

`

export const VerticalGradient = styled.div`

    width: inherit;
    height: inherit;
    background: linear-gradient(to top, #111 10%, transparent 90%);

`
export const HorizontalGradient = styled.div`

    width: inherit;
    height: inherit;
    background: linear-gradient(to right, #111 20%, transparent 80%);

`

export const Name = styled.div` 

    font-size: 60px;
    font-weight: bold;

`

export const Info = styled.div` 

    font-size: 18px;
    font-weight: bold;
    margin-top: 15px;

`

export const Points = styled.div` 

    display: inline-block;
    color: #46d369;
    margin-right: 15px;

`

export const Year = styled.div` 

    display: inline-block;
    margin-right: 15px;

`

export const Seasons = styled.div` 

    display: inline-block;


`

export const Description = styled.div` 

    margin-top: 15px;
    font-size: 20px;
    color: #999;
    max-width: 40%;

`

export const Buttons = styled.div` 
    
    margin-top: 15;

`

export const Genres = styled.div` 

    



`

export const ButtonWatch = styled.a` 

    display: inline-block;
    font-size: 20px;
    font-weight: bold;
    padding: 15px 25px;
    border-radius: 5px;
    text-decoration: none;
    margin-right: 10px;

`

