import styled, { css } from "styled-components";



export const Container = styled.div`
    
    ${({movieOverview})=>css`
        ${movieOverview && `
            opacity: 0.3;
            overflow: hidden;
        `}
    `}

`

export const Lists = styled.section`

    margin-top: -150px;


`


export const teste = styled.div`

    z-index: 112333131;
    width: 100px;
    height: 100px;
    position: absolute;
    top: 50vh;
    left: 100vh;
    background: red;
    opacity: 1;
    backdrop-filter: blur(1000px);


`
