import styled from "styled-components";


export const Row = styled.div`

    margin-bottom: 30px;

    h2{
        margin-left: 30px ;
    }

`



export const ListArea = styled.div`

    overflow-x: hidden;


`

export const List = styled.div`
    
    width: 999999p;


`


export const Item = styled.div`

    display: inline-block;

`
