import { useEffect } from 'react'

export const useClickOut = ( setIsOpen, Ref, Ref2 = '')=>{

  
  
  useEffect(() => {
    let RefElement =''
    
    if(Ref2 === ''){
      RefElement = false  
    }else{
      RefElement = Ref2
    }
    const handleOutClick = (event)=>{
          if(!Ref.contains(event.target) && !RefElement.contains(event.target)){
            setIsOpen(false)
        }}
    
        document.addEventListener('mousedown', handleOutClick)
    
        return ()=>{
          document.removeEventListener('mousedown', handleOutClick)
        }
      }, [setIsOpen, Ref])

}
