import styled, { css } from "styled-components"

export const OverViewContainer = styled.div` 

    width: 100%;
    height: 100vh;
    position: fixed;
    top: 0;
    left: 0;
    background: rgba(0, 0, 0, 0.4);
    z-index: 9999999;
    ${({isOpen})=> css` 
        ${!isOpen ? 'display: none' : 'display: block'}
    `}

`


export const OverView = styled.div` 

    width: 60vh;
    height: 90vh;
    background: red;
    z-index: 10000000;
    position: absolute;
    top: 50px;
    left: 35%;

    ${({isOpen})=> css` 
        ${!isOpen && 'display: none;'}
    `}

`
