import styled from "styled-components"


export const Item = styled.div`

    display: inline-block;
    width: 150px;

    img{
        width: 100%;
        transform: scale(0.9);
        transition: 0.2s ease-in-out;
    }

    img:hover{
        transform: scale(1);
    }
`
