import { useEffect } from 'react'

export const useClickOut = ( setIsOpen, Ref, Ref2 = '')=>{

    let RefElemnt

    if(Ref2 === ''){
      let RefElemnt = false  
    }else{
      let RefElemnt = Ref2
    }


    useEffect(() => {
        const handleOutClick = (event)=>{
          if(!Ref.contains(event.target) && !RefElemnt.contains(event.target)){
            setIsOpen(false)
        }}
    
        document.addEventListener('mousedown', handleOutClick)
    
        return ()=>{
          document.removeEventListener('mousedown', handleOutClick)
        }
      }, [setIsOpen, Ref])

}
