import styled, { css } from "styled-components";



export const Container = styled.div`
    
    

    

`

export const Lists = styled.section`

    margin-top: -150px;

    
`

export const OverViewContainer = styled.div` 

    width: 100%;
    height: 100vh;
    position: absolute;
    top: 0;
    left: 0;
    background: #000;
    z-index: 99999999;
    ${({isOpen})=> css` 
        ${isOpen ? 'display: none' : ''}
    `}

`


export const OverView = styled.div` 

    width: 100px;
    height: 100px;
    background: red;
    z-index: 10000000;
    position: absolute;
    top: 50%;
    left: 50%;

    ${({isOpen})=> css` 
        ${!isOpen && 'display: none;'}
    `}

`
