import styled, { css } from "styled-components";



export const Container = styled.div`
    
    ${({isOpen})=> css` 
        ${isOpen ? 'opacity: 0.4;' : 'opacity: 1;'}
    `}


    
`

export const Lists = styled.section`

    margin-top: -150px;
    

    
`
