import * as Styled from './styles'
import { MoivieCard } from '../MovieCard'
import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore'
import NavigateNextIcon from '@material-ui/icons/NavigateNext'
import { useRef, useState} from 'react'

export const MovieRow = ({  title, items }) =>{

    const [ buttonReveal, setButtonReveal] = useState(false)
    const buttonsDiv = useRef(null)




    return(
        <Styled.Row>
            <h2>{title}</h2>


                <Styled.MoveRowLeft>
                    <NavigateBeforeIcon style={{fontSize: 50}}/>
                </Styled.MoveRowLeft>
                <Styled.MoveRowRight>
                    <NavigateNextIcon style={{fontSize: 50}}/>
                </Styled.MoveRowRight>


            <Styled.ListArea ref={buttonsDiv.current}>
                <Styled.List>
                    {items.results.length > 0 && items.results.map((item, key)=>{
                        return (
                            <MoivieCard key={key} item = {item}/>
                        )
                    })}
                </Styled.List>
            </Styled.ListArea>
        </Styled.Row>
    )
}
