/* eslint-disable default-case */
const API_KEY = "52d4c6a9b3e461b254b29e1ad7d9a52e"
const API_BASE = "https://api.themoviedb.org/3"


const basicFetch = async(url) =>{

    const req = await fetch(`${API_BASE}${url}`)
    const json = await req.json()

    return json;

}


export const getHomeList = async()=>{
    return [
        {
            slug: 'originals',
            title: 'Orininais Netflix',
            items: await basicFetch(`/discover/tv?with_network=213&language=pt-BR&api_key=${API_KEY}`)
        },
        {
            slug: 'trending',
            title: 'Recomendados para você',
            items: await basicFetch(`/trending/all/week?language=pt-BR&api_key=${API_KEY}`)
        },
        {
            slug: 'toprated',
            title: 'Em alta',
            items: await basicFetch(`/movie/top_rated?language=pt-BR&api_key=${API_KEY}`)
        },
        {
            slug: 'action',
            title: 'Ação',
            items: await basicFetch(`/discover/movie?with_genres=28&language=pt-BR&api_key=${API_KEY}`)
        },
        {
            slug: 'comedy',
            title: 'Comédia',
            items: await basicFetch(`/discover/movie?with_genres=35&language=pt-BR&api_key=${API_KEY}`)
        },
        {
            slug: 'horror',
            title: 'Terror',
            items: await basicFetch(`/discover/movie?with_genres=27&language=pt-BR&api_key=${API_KEY}`)
        },
        {
            slug: 'romance',
            title: 'Romance',
            items: await basicFetch(`/discover/movie?with_genres=10749&language=pt-BR&api_key=${API_KEY}`)
        },
        {
            slug: 'documentary',
            title: 'Documentários',
            items: await basicFetch(`/discover/movie?with_genres=99&language=pt-BR&api_key=${API_KEY}`)
        },
    ]
}


export const getMovie = async(movieId, type)=>{


    let info = {}

    if(movieId){
        switch (type){
            case 'movie':
                info = await basicFetch(`/movie/${movieId}?langueage=pt-BR&api_key=${API_KEY}`)
                break
            case 'tv':
                info = await basicFetch(`/tv/${movieId}?langueage=pt-BR&api_key=${API_KEY}`)
                break
        }
    }

    return info


}
