import styled from "styled-components";

export const Menu = styled.div` 

    border-radius: 0px 0px 10px 10px;
    width: 200px;
    height: 200px;
    position: fixed;
    background-color: #111;
    top: 70px;
    right: 10px;
    display: flex;
    align-items: center;
    

    ul{
        margin: 0  auto;
        list-style: none;
    }

    ul li{
        padding-bottom: 15px;
    }

    ul li a{
        color: #fff;
        text-decoration: none;
        font-size: 20px;
    }

    ul li a:hover{
        opacity: 0.8;
    }

    &::after{
        
    }


`
